<?php	session_start();		class Database	{		private static $database;		public static $apiaddress = "http://tubes4asdasd.aws.af.cm/api.php/";//http://localhost/GitHub/IF3038-2013/src/server/api.php/";//"http://tubes4asdasd.aws.af.cm/api.php/";				public static function handler()		{			if (!self::$database)			{				try {					/*					$services_json = json_decode(getenv("VCAP_SERVICES"),true);					$mysql_config = $services_json["mysql-5.1"][0]["credentials"];					$username = $mysql_config["username"];//"root"; //$mysql_config["username"];					$password = $mysql_config["password"];//""; //$mysql_config["password"];					$hostname = $mysql_config["hostname"];//"127.0.0.1"; //$mysql_config["hostname"];					$port = $mysql_config["port"];//3306; //$mysql_config["port"];					$db = $mysql_config["name"];//"progin_439_13508105"; //$mysql_config["name"];					*/					$username = "root"; //$mysql_config["username"];					$password = ""; //$mysql_config["password"];					$hostname = "127.0.0.1"; //$mysql_config["hostname"];					$port = 3306; //$mysql_config["port"];					$db = "progin_439_13508105"; //$mysql_config["name"];					self::$database = new PDO("mysql:host=$hostname;dbname=$db;port=$port", $username, $password);					self::$database->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);					self::$database->setAttribute(PDO::ATTR_DEFAULT_FETCH_MODE, PDO::FETCH_ASSOC);									} 				catch (PDOException $e) 				{					die('Connection error: ' . $e->getMessage());				}			}						return self::$database;		}	}		function query($query, $data)	{		$connection = Database::handler();		$statement = $connection->prepare($query);		$statement->execute($data);				return $statement->fetch();	}		function query2($query, $data)	{		$connection = Database::handler();		$statement = $connection->prepare($query);		$statement->execute($data);	}		function queryAll($query, $data)	{		$connection = Database::handler();		$statement = $connection->prepare($query);		$statement->execute($data);				return $statement->fetchAll();	}?>