<?php	session_start();		class Database	{		private static $database;		public static $apiaddress = "http://localhost/tubes4/src/server/api.php";				public static function handler()		{			if (!self::$database)			{				try {					$services_json = json_decode(getenv("VCAP_SERVICES"),true);					$mysql_config = $services_json["mysql-5.1"][0]["credentials"];					$username = $mysql_config["username"];					$password = $mysql_config["password"];					$hostname = $mysql_config["hostname"];					$port = $mysql_config["port"];					$database = $mysql_config["name"];					self::$database = new PDO("mysql:host=$hostname;dbname=$database;port=$port", $username, $password);					self::$database->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);					self::$database->setAttribute(PDO::ATTR_DEFAULT_FETCH_MODE, PDO::FETCH_ASSOC);									} 				catch (PDOException $e) 				{					die('Connection error: ' . $e->getMessage());				}			}						return self::$database;		}	}		function query($query, $data)	{		$connection = Database::handler();		$statement = $connection->prepare($query);		$statement->execute($data);	}?>